import { Component, OnInit } from '@angular/core';
import { TaskService } from '../task.service';

@Component({
  selector: 'app-task-list',
  templateUrl: './task-list.component.html',
  styleUrls: ['./task-list.component.css'],
})
export class TaskListComponent implements OnInit {
  tasks: any[] = [];
  newTask: any = {};

  constructor(private taskService: TaskService) {}

  ngOnInit(): void {
    this.loadTasks();
  }

  loadTasks() {
    this.taskService.getTasks().subscribe((data: any) => {
      this.tasks = data;
    });
  }

  editTask(task: any) {
    if (task.editing) {
      // Guardar la tarea editada
      this.taskService.updateTask(task.id, task).subscribe(() => {
        task.editing = false;
      });
    } else {
      // Habilitar el modo de edición
      task.editing = true;
    }
  }

  deleteTask(taskId: number) {
    const confirmDelete = confirm('¿Estás seguro de que deseas eliminar esta tarea?');

    if (confirmDelete) {
      this.taskService.deleteTask(taskId).subscribe(() => {
        this.loadTasks();
      });
    }
  }

  createTask() {
    this.taskService.createTask(this.newTask).subscribe(() => {
      this.newTask = {};
      this.loadTasks(); 
    });
  }

}
<div class="bg-blue-200 p-4">
  <div class="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
    <div *ngFor="let task of tasks" class="bg-white shadow-md rounded-lg p-4">
      <!-- Contenido de la card de la tarea -->
      <div class="bg-blue-200 text-blue-900 font-semibold mb-2 p-2 rounded">
        {{ task.title }}
      </div>
      <p class="mt-2">{{ task.description }}</p>
      <div class="mt-4">
        <button (click)="editTask(task)" class="px-4 py-2 bg-blue-500 text-white rounded-md">
          {{ task.editing ? 'Guardar' : 'Editar' }}
        </button>
        <button (click)="deleteTask(task.id)" class="px-4 py-2 bg-red-500 text-white rounded-md">Eliminar</button>
      </div>
    </div>
  </div>

  <form (submit)="createTask()" #taskForm="ngForm">
    <div class="mt-4">
      <label for="title">Título:</label>
      <input type="text" id="title" name="title" [(ngModel)]="newTask.title" required>
    </div>
    <div class="mt-4">
      <label for="description">Descripción:</label>
      <textarea id="description" name="description" [(ngModel)]="newTask.description" required></textarea>
    </div>
    <div class="mt-4">
      <button type="submit" class="px-4 py-2 bg-green-500 text-white rounded-md">Agregar Tarea</button>
    </div>
  </form>
</div>
