import { Component, OnInit } from '@angular/core';
import { TaskService } from '../task.service';

@Component({
  selector: 'app-task-list',
  templateUrl: './task-list.component.html',
  styleUrls: ['./task-list.component.css'],
})
export class TaskListComponent implements OnInit {
  tasks: any[] = [];

  constructor(private taskService: TaskService) {}

  ngOnInit(): void {
    this.taskService.getTasks().subscribe((data: any) => {
      this.tasks = data;
    });
  }
}
<div class="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
  <div *ngFor="let task of tasks" class="bg-blue-200 shadow-md rounded-lg p-4">
    <div class="bg-blue-200 text-red font-semibold mb-2 p-2 rounded">
      {{ task.title }}
    </div>
    <p class="mt-2">{{ task.description }}</p>
    <div class="mt-4">
      <button class="px-4 py-2 bg-blue-500 text-white rounded-md">Editar</button>
      <button class="px-4 py-2 bg-red-500 text-white rounded-md">Eliminar</button>
    </div>
  </div>
</div>
